import xlrd
import requests
import xlwt
from bs4 import BeautifulSoup

workbook = xlwt.Workbook()
worksheet = workbook.add_sheet('安全生产许可证信息', cell_overwrite_ok=True)

def get_html(url, num):
    headers = {
        'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/78.0.3904.108 Safari/537.36'
    }
    params = {
        "__EVENTTARGET": "AspNetPager2",
        "__EVENTARGUMENT": num,
        "__VIEWSTATE": "",
        "__VIEWSTATEGENERATOR": "AB12D588",
        "__EVENTVALIDATION": "/wEdAAn4uMrNv1GN5xHYyNxjY+mlcyCwLNtjiGsUsD1klKe8mO/27pz3VRsK7NDdBcWPH1oVz/HNqEavkdJhHEQf0CC9QF5FF4kumzRC1Hm6gbSZLJSStlQIejt9Eiz2dXvmYMkdxEWiDJrToSQwV2qIPrDYCSAehAWh8K/R7+KNdzUNgpSYA6QBBGuDgl6JiXAF2qIuOK+UQVCRazqza48ZKfpBfBeHGajgPpe7w2qzYRJB7g==",
        "CretType": "建筑施工企业",
        "ry_reg_type": "411800"
    }
    response = requests.post(url, params, headers)
    html = response.text
    return html


xl = xlrd.open_workbook('/Users/mac/PycharmProjects/茂盛数据爬虫代码/src/social_id.xls')
table = xl.sheets()[0]

index = 0
for i in range(0, 30821):
    rowData = table.cell(i ,0).value
    print(index)
    # rowData = '914103004156240779'
    soup = BeautifulSoup(get_html("http://hngcjs.hnjs.gov.cn/company/Aqscxkz?tyshxydm=" + rowData, 'lxml'))
    divContent = soup.findAll(name='div', attrs={"class": "news_con"})

    originStr = ""
    spans = divContent[0].select("span")
    worksheet.write(index, 0, rowData)
    for sIndex, span in enumerate(spans):
        worksheet.write(index, sIndex+1, span.string)
    index = index + 1
    workbook.save("安全许可证.xls")