import requests
from bs4 import BeautifulSoup
import xlwt

workbook = xlwt.Workbook()
worksheet = workbook.add_sheet('sheet')

arrays =[]

def get_html(url,num):
    headers = {
        'User-Agent':'Mozilla/5.0(Macintosh; Intel Mac OS X 10_11_4)\
        AppleWebKit/537.36(KHTML, like Gecko) Chrome/52 .0.2743. 116 Safari/537.36'
    }
    params ={
        "__EVENTTARGET":"AspNetPager2",
        "__EVENTARGUMENT":num,
        "__VIEWSTATE":"",
        "__VIEWSTATEGENERATOR":"AB12D588",
        "__EVENTVALIDATION":"/wEdAAn4uMrNv1GN5xHYyNxjY+mlcyCwLNtjiGsUsD1klKe8mO/27pz3VRsK7NDdBcWPH1oVz/HNqEavkdJhHEQf0CC9QF5FF4kumzRC1Hm6gbSZLJSStlQIejt9Eiz2dXvmYMkdxEWiDJrToSQwV2qIPrDYCSAehAWh8K/R7+KNdzUNgpSYA6QBBGuDgl6JiXAF2qIuOK+UQVCRazqza48ZKfpBfBeHGajgPpe7w2qzYRJB7g==",
		"CretType":"建筑施工企业"
    }
    response = requests.post(url,params,headers)
    html = response.text
    print(html)
    return html

for i in range(1,2):
    #print(i)
    soup = BeautifulSoup(get_html("http://hngcjs.hnjs.gov.cn/SiKuWeb/QiyeList.aspx?type=qyxx&val=",i+1),'lxml')

    for tbody in soup.find_all("tbody"):
        trlist = tbody.find_all('tr')
        for tr in trlist:

            tblist = tr.find_all('td')
            for tb in tblist:
                if tb.string == None:
                    pass
                else:
                    arrays.append(tb.string)

    #arrays.pop(i*20*5)
#print(arrays)

for i in range(len(arrays)):
    print(arrays[i])
    worksheet.write(int(i/5), i%5, arrays[i])


